        return (float) (1 + Math.sin(Math.PI * t / rising_time));
    }

    private float secret_wave(double t, float a, float b) {

        float r = (float) ((t + (a / 2)) % (a + b));
        float t_prime = r - (a / 2);

        if (r <= a) {
            return sine(t_prime, a);
        } else {
            return sine(t_prime - ((a - b) / 2), b);
        }
