    int [] parr = Arrays.copyOf(parrOrig, arrsize);
    int lines = 0;
    while(lines < 1000)
    {
        int[] oldarr = Arrays.copyOf(parr,arrsize);

        for(int i = 0; i < arrsize; i++)
            screen.append(" " + oldarr[i]);
