  x*(-x)^n*Exp[
     Limit[Zeta[s]*
       Sum[(1 - If[Mod[k, n] == 0, n, 0])/k^(s - 1), {k, 1, n}], 
      s -> 1]]/(n + 1), {n, 1, 12}]
Series[x/LambertW[x], {x, 0, 12}] Edit 2.10.2013: Integration is better: Clear[x, n, k, s, a1, nn, b1]
b1 = Expand[
   Sum[Exp[Limit[
       1/(s - 1)*
        Sum[(1 - If[Mod[k, n] == 0, n, 0])/(k)^(s - 1), {k, 1, 4*n}], 
       s -> 1]]*(-x)^n, {n, 0, 32}]];
a1 = 1 + Integrate[b1, x];
x = N[(1 - 11/8)/Exp[1], 30];
Print["here"]
N[2*Pi*Exp[1]*a1, 30]
N[2*Pi*Exp[1]*x/LambertW[x], 30]

