     glVertex3f(0.0, 500.0, 0.0);
     // z
     glVertex3f(0.0, 0.0, -500.0);
     glVertex3f(0.0, 0.0, 500.0);

   glEnd(); 

   cout << endl;

   double previousTheta = 0.0;

   for( int i=0; i<=sweepResolutionMod; i++ )
   {
     double theta = i>0 ? (360/sweepResolutionMod)*i : 0;

     theta = theta * (M_PI/180);

     if( clickedPoints.size() > 1 )
     {
       // the 'vertical' piece
       glBegin(GL_LINE_STRIP);

       for(int i=0; i<clickedPoints.size(); i++ )
       {     
         // normalize
         GLfloat tempX = (clickedPoints[i].x-250)/250;
         GLfloat tempY = (clickedPoints[i].y-250)/250;
         GLfloat tempZ = 0.0;

         // new points
         GLfloat newX = ( tempX * cos(theta) ) + ( tempZ * sin(theta) );
         GLfloat newY = tempY;
         GLfloat newZ = ( tempZ * cos(theta) ) - ( tempX * sin(theta) );

         glVertex3f(newX, newY, newZ);     
       }

       glEnd();

       // the 'horizontal' piece
       if( previousTheta != theta )
       {
         glBegin(GL_LINES);

         for(int i=0; i<clickedPoints.size(); i++ )
         {     
           // normalize
           GLfloat tempX = (clickedPoints[i].x-250)/250;
           GLfloat tempY = (clickedPoints[i].y-250)/250;
           GLfloat tempZ = 0.0;

           // new points
           GLfloat newX = ( tempX * cos(theta) ) + ( tempZ * sin(theta) );
           GLfloat newY = tempY;
           GLfloat newZ = ( tempZ * cos(theta) ) - ( tempX * sin(theta) );

           // previous points
           GLfloat previousX = ( tempX * cos(previousTheta) ) + ( tempZ * sin(previousTheta) );
           GLfloat previousY = tempY;
           GLfloat previousZ = ( tempZ * cos(previousTheta) ) - ( tempX * sin(previousTheta) );

           // horizontal component           
           glVertex3f(newX, newY, newZ);     
           glVertex3f(previousX, previousY, previousZ);     
         }

         glEnd();
       }
     }

     previousTheta = theta;
   }

   glutSwapBuffers();
}

The topologically invariant Euler characteristic of a 2-surface is given by $\chi=\frac{1}{4\pi}\int\sqrt{g}\mathcal{R}$ (where $\mathcal{R}$ is the scalar curvature) and is equivalent to $\chi=2-2g - b$ for a surface with $g$ handles and $b$ boundaries. For a sphere this is simple, $\sqrt{g}=R^2$ and $\mathcal{R}=\frac{2}{R^2}$, where $R$ is the radius. Therefore, $\chi=2$, as suggested by the second formula above. If we wanted to add a boundary, we could take the usual metric
\begin{equation}
\mathrm{d}s^2=\mathrm{d}\theta^2 + \sin^2{\theta} \mathrm{d}\phi^2
\end{equation}
and limit $\theta$ to $0\leq\theta\leq\theta_0$, with $\theta_0<\pi$. We have added a hole in the sphere, thus adding a boundary, so the Euler charateristic should be 1. By the first formula, however, we get $\chi=(1-\cos^{-1}{\theta_0})$. Moreover, this should be topologically equivalent to a flat disc, on which we may take a flat metric, so $\mathcal{R}=0$ and $\chi=0$. Where have I gone wrong? Many thanks in advance.
